import React from 'react';
import { useRoute } from '@react-navigation/native';
import {
  FlatList,
  ImageBackground,
  StyleSheet, 
  Text, 
  View,
  SafeAreaView,
  ScrollView,
} from 'react-native';
import BackImage from '../assets/airplane-2748431_960_720.png';
import { MASKAPAI, BANDARA, JADWAL } from '../data/Data';

const Screen2 = ({navigation}) => {
  const route = useRoute();
  return (
    <SafeAreaView >
        <View style={styles.boxtop}>
            <Text style={styles.namaapk}>mrar.id</Text>
            <Text style={styles.textSubtittle}>Hasil Pencarian Penerbangan</Text>
            <Text style={styles.textSubtittle}>(Tanggal Keberangkatan)</Text>
        </View>
        <View style={styles.marginMain}>                  
            <FindData/>
        </View>
    </SafeAreaView>
  )
  function NoHaveData() {
    return (        
      <Text style={styles.teksDiBox}>Maaf Hasil Pencarian Tidak Ditemukan</Text>  
    );
  }


  function FindData(){
    if (route.params.keberangkatan === '' || route.params.tujuan === '' || route.params.waktu === ''){
        return NoHaveData();
    }else{
        const keberangkatanID = BANDARA.find(id => id.bandara_nama.toLowerCase() === route.params.keberangkatan.toLowerCase()).bandara_id;
        const tujuanID = BANDARA.find(id => id.bandara_nama.toLowerCase() === route.params.tujuan.toLowerCase()).bandara_id;
        const hasil = JADWAL.filter(id => id.bandara_id_keberangkatan.toLowerCase() === keberangkatanID.toLowerCase() && id.bandara_id_kedatangan.toLowerCase() === tujuanID.toLowerCase() && id.tanggal === route.params.waktu.toLowerCase());

        let findAll = (hasil.length >= 1) ? DataGet(hasil) : NoHaveData() ;

        return findAll;
    }
    }
}
const styles = StyleSheet.create({

    namaapk: {
        marginTop: 30,
        marginBottom: 20,
        fontSize: 30,
        textAlign: 'center',
        fontWeight: "bold",
        color: "white"
    },

    teksDiBox: {
        color: "black",
        alignSelf: 'flex-start',
        fontSize: 15,
        fontWeight: "bold",
        paddingVertical: 5,
    },
      
    row:{
        flexDirection: "row",
        justifyContent: 'space-between',
        backgroundColor: 'powderblue',
        
    },
    boxtop:{
        alignItems:'center',
        //justifyContent:'center',
        backgroundColor: 'gray',
        height: 250,
    },
    textSubtittle:{
        fontFamily: "Cochin",
        fontSize: 16,
        fontWeight: "bold",
    },
    textTitle:{
        fontFamily: "Cochin",
        fontSize: 32,
        fontWeight: "bold",
    },
    marginMain:{
        marginVertical: 20,
        marginHorizontal: 10,
    },
    viewCard:{
        flexDirection: "row",
        padding: 10,
    },
    viewImage:{
        flex: 1,
    },
});


export default Screen2;
