    fontSize: 15,
    fontWeight: "bold",
    paddingVertical: 5,
    },

  inputteks: {
    height: 40,
    width: 260,
    borderWidth: 1,
    borderRadius: 5,
    borderColor: '#555',
    marginBottom: 5, 
    fontSize: 10,
    fontWeight: 'bold',
  },

  tombol:{
    height: 35,
    width: 260,
    borderRadius: 5,
    backgroundColor: "orange", 
    paddingVertical: 7,
    marginTop: 20
  },

  tekstombol: {
    fontsize: 15,
    color: '#fff',
    fontWeight: '500',
    textAlign: 'center'
  },

  namanim: {
    fontSize: 15,
    textAlign: 'center',
    fontWeight: "bold",
    color: "black",
  },
  Hasil : {
    position: 'absolute',
    left: 15,
    top: 5,
    fontWeight:'Bold',
  },

  Hasil2 : {
    fontSize: 21,
    position: 'absolute',
    left: 40,
    top: 65,
    fontWeight:'Bold',
  },

  Hasil3 : {
    fontsize: 20,
    position: 'absolute',
    left: 237,
    top: 73,
    fontWeight: 'Bold',
    color: 'blue',
  },

  imagestyle:{
    height: 40,
    width: 40,
  },

  boxlist: {
    width: 350,
    height: 100,
    marginBottom: 40,
    borderRadius: 5,
    shadowOpacity: 0.5,
    shadowRadius: 15.5,
    backgroundColor: "white",
  }
});

export default App;
