import React, {useState} from "react";
import {
  FlatList,
  ImageBackground,
  StyleSheet, 
  Text, 
  TextInput, 
  TouchableOpacity, 
  View,
} from 'react-native';
import BackImage from './assets/airplane-2748431_960_720.png';
import { NavigationContainer } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";
import * as SecureStore from 'expo-secure-store';

const stack = createStackNavigator();

function ScreenA({ navigation }){
  const onPressHandler = () => {
    navigation.navigate('Hasil Pencarian');
  }
  const [keberangkatan, onChangeKeberangkatan] = React.useState('')

  async function save(keberangkatan){
    await SecureStore.setItemAsync(keberangkatan);
  }

  return(
    <ImageBackground source={BackImage} style={styles.container}>
      <Text style={styles.namaapk}>mrar.id</Text>
      <View style={styles.bgbox}>

        <Text style={styles.teksDiBox}>Lokasi Keberangkatan</Text>
        <TextInput 
        style={styles.inputteks} 
        placeholder='  masukkan lokasi keberangkatan'
        onChangeText={text => onChangeKeberangkatan(text)}
        value={keberangkatan} 
        />
        {/* <Text style={styles.teksDiBox}>Lokasi Tujuan</Text>
        <TextInput 
        style={styles.inputteks} 
        placeholder='  masukkan lokasi Tujuan' />    

        <Text style={styles.teksDiBox}>Tanggal Keberangkatan</Text>
        <TextInput 
        style={styles.inputteks} 
        placeholder='  masukkan Tanggal keberangkatan'/> */}

        <TouchableOpacity
          onPress={onPressHandler}
          style={styles.tombol}>
          <Text style={styles.tekstombol}>Cari</Text>
        </TouchableOpacity>

      </View>
        <Text style={styles.namanim}>Copyright: M. Rafli - 119140124</Text>
    </ImageBackground>
  )
}

function ScreenB(){
  const [Penerbangan, setPenerbangan] = useState([
    { keberangkatan: 'Lampung', Tujuan: 'Jakarta', maskapai:'Kuda Terbang Air', Jadwal: '2022-08-11', key: '1' },
    { keberangkatan: 'Lampung', Tujuan: 'Palembang', maskapai:'Naga Indosiar Air', Jadwal: '2022-08-12', key: '2' },
    { keberangkatan: 'Lampung', Tujuan: 'Jakarta', maskapai:'Buraq Air', Jadwal: '2022-08-12', key: '3' },
    { keberangkatan: 'Palembang', Tujuan: 'Jakarta', maskapai:'Kuda Terbang Air', Jadwal: '2022-08-13', key: '4' },
    { keberangkatan: 'Lampung', Tujuan: 'Jakarta', maskapai:'Cheetah Air', Jadwal: '2022-08-11', key: '5' },
    { keberangkatan: 'Jakarta', Tujuan: 'Palembang', maskapai:'Cheetah Air', Jadwal: '2022-08-13', key: '6' },
    { keberangkatan: 'Palembang', Tujuan: 'Lampung', maskapai:'Gendong Air', Jadwal: '2022-08-11', key: '7' },
    { keberangkatan: 'Jakarta', Tujuan: 'Lampung', maskapai:'Naga Indosiar Air', Jadwal: '2022-08-12', key: '8' },
    { keberangkatan: 'Jakarta', Tujuan: 'Palembang', maskapai:'Jetpack Air', Jadwal: '2022-08-13', key: '9' },
    { keberangkatan: 'Lampung', Tujuan: 'Jakarta', maskapai:'Ikan TPI Air', Jadwal: '2022-08-11', key: '10' },
    { keberangkatan: 'Palembang', Tujuan: 'Lampung', maskapai:'Buraq Air', Jadwal: '2022-08-12', key: '11' },
    { keberangkatan: 'Palembang', Tujuan: 'Jakarta', maskapai:'Garuda Didadaku Air', Jadwal: '2022-08-11', key: '12' },
    { keberangkatan: 'Lampung', Tujuan: 'Jakarta', maskapai:'Gojek Air', Jadwal: '2022-08-13', key: '13' },
    { keberangkatan: 'Jakarta', Tujuan: 'Palembang', maskapai:'Maxim Air', Jadwal: '2022-08-12', key: '14' },
    { keberangkatan: 'Palembang', Tujuan: 'Jakarta', maskapai:'Ikan TPI Air', Jadwal: '2022-08-11', key: '15' },
    { keberangkatan: 'Jakarta', Tujuan: 'Lampung', maskapai:'Maxim Air', Jadwal: '2022-08-12', key: '16' },
    { keberangkatan: 'Lampung', Tujuan: 'Jakarta', maskapai:'Kuda Terbang Air', Jadwal: '2022-08-13', key: '17' },
    { keberangkatan: 'Jakarta', Tujuan: 'Palembang', maskapai:'Buraq Air', Jadwal: '2022-08-13', key: '18' },
    { keberangkatan: 'Palembang', Tujuan: 'Lampung', maskapai:'Kuda Terbang Air', Jadwal: '2022-08-12', key: '19' },
    { keberangkatan: 'Jakarta', Tujuan: 'Lampung', maskapai:'Cheetah Air', Jadwal: '2022-08-11', key: '20' },
  ]);
  return (
    <ImageBackground source={BackImage} style={styles.container}>
      <FlatList
          data={Penerbangan}
          renderItem={({ item }) => (
            <View style={styles.boxlist}>
              <Text style={styles.Hasil}>({item.keberangkatan})   -   ({item.Tujuan})</Text>
              <Text style={styles.Hasil2}>{item.maskapai}</Text>
              <Text style={styles.Hasil3}>({item.Jadwal})</Text>
            </View>
          )}
      />
    </ImageBackground>
  )
}


const App = () => (
  <NavigationContainer>
    <stack.Navigator>
      <stack.Screen
        name="Screen_A"
        component={ScreenA}
        options={{
        header: () => null
        }}
      />
      <stack.Screen
        name="Hasil Pencarian"
        component={ScreenB}
      />
    </stack.Navigator>
  </NavigationContainer>
); 

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 24,
    alignItems: 'center'
  },

  namaapk: {
    marginTop: 50,
    fontSize: 30,
    textAlign: 'center',
    fontWeight: "bold",
    color: "white"
  },

  bgbox: {
    width: 300,
    marginTop: 50,
    paddingVertical: 20,
    paddingHorizontal: 23,
    borderRadius: 10,
    shadowOpacity: 0.5,
    shadowRadius: 15.5,
    backgroundColor: "white",
    alignItems: 'center'

  },
  teksDiBox: {
    color: "black",
    alignSelf: 'flex-start',
    fontSize: 15,
    fontWeight: "bold",
    paddingVertical: 5,
    },

  inputteks: {
    height: 40,
    width: 260,
    borderWidth: 1,
    borderRadius: 5,
    borderColor: '#555',
    marginBottom: 5, 
    fontSize: 10,
    fontWeight: 'bold',
  },

  tombol:{
    height: 35,
    width: 260,
    borderRadius: 5,
    backgroundColor: "orange", 
    paddingVertical: 7,
    marginTop: 20
  },

  tekstombol: {
    fontsize: 15,
    color: '#fff',
    fontWeight: '500',
    textAlign: 'center'
  },

  namanim: {
    fontSize: 15,
    textAlign: 'center',
    fontWeight: "bold",
    color: "black",
  },
  Hasil : {
    position: 'absolute',
    left: 15,
    top: 5,
    fontWeight:'Bold',
  },

  Hasil2 : {
    fontSize: 21,
    position: 'absolute',
    left: 40,
    top: 65,
    fontWeight:'Bold',
  },

  Hasil3 : {
    fontsize: 20,
    position: 'absolute',
    left: 237,
    top: 73,
    fontWeight: 'Bold',
    color: 'blue',
  },

  imagestyle:{
    height: 40,
    width: 40,
  },

  boxlist: {
    width: 350,
    height: 100,
    marginBottom: 40,
    borderRadius: 5,
    shadowOpacity: 0.5,
    shadowRadius: 15.5,
    backgroundColor: "white",
  }
});

export default App;
