import React from 'react';
import { useRoute } from '@react-navigation/native';
import {
  FlatList,
  StyleSheet, 
  Text, 
  View,
  SafeAreaView,
} from 'react-native';
import { BANDARA, MASKAPAI, JADWAL } from '../data/Data';

const Screen2 = ({navigation}) => {
  const route = useRoute();
  return (
    <SafeAreaView >
        <View style={styles.boxtop}>
            <Text style={styles.namaapk}>mrar.id</Text>
            <Text style={styles.textSubtittle}>Hasil Pencarian Penerbangan</Text>
            <Text style={styles.textSubtittle}>(Tanggal Keberangkatan)</Text>
        </View>
        <View style={styles.marginMain}>                  
            <FindData/>
        </View>
    </SafeAreaView>
  )
  function NoHaveData() {
    return (        
      <Text style={styles.teksDiBox}>Maaf Hasil Pencarian Tidak Ditemukan</Text>  
    );
  }

  function DataGet (hasil){
    return (
        <FlatList
            data = {hasil}
            renderItem={({item}) => (
                <View style={styles.boxlist}>
                    <Text style={styles.Hasil}>{BANDARA.find(sub => sub.bandara_id == item.bandara_id_keberangkatan).bandara_nama}  {'->'}  {BANDARA.find(sub => sub.bandara_id === item.bandara_id_kedatangan).bandara_nama}</Text>
                    <Text style={styles.Hasil2}>{MASKAPAI.find(sub => sub.maskapai_id === item.maskapai_id).maskapai_nama}</Text>
                    <Text style={styles.Hasil3}>{JADWAL.find(sub => sub.tanggal === item.tanggal).tanggal}</Text>
                </View>
            )}
            keyExtractor={item => item.jadwal_id}
        />
    );
};


  function FindData(){
    if (route.params.keberangkatan === '' || route.params.tujuan === '' || route.params.waktu === ''){
        return NoHaveData();
    }else{
        const keberangkatanID = BANDARA.find(id => id.bandara_nama.toLowerCase() === route.params.keberangkatan.toLowerCase()).bandara_id;
        const tujuanID = BANDARA.find(id => id.bandara_nama.toLowerCase() === route.params.tujuan.toLowerCase()).bandara_id;
        const hasil = JADWAL.filter(id => id.bandara_id_keberangkatan.toLowerCase() === keberangkatanID.toLowerCase() && id.bandara_id_kedatangan.toLowerCase() === tujuanID.toLowerCase() && id.tanggal === route.params.waktu.toLowerCase());

        let findAll = (hasil.length >= 1) ? DataGet(hasil) : NoHaveData() ;

        return findAll;
        }
    }
}
const styles = StyleSheet.create({

    namaapk: {
        marginTop: 20,
        marginBottom: 20,
        fontSize: 30,
        textAlign: 'center',
        fontWeight: "bold",
        color: "white"
    },

    teksDiBox: {
        color: "black",
        alignSelf: 'flex-start',
        fontSize: 15,
        fontWeight: "bold",
        paddingVertical: 5,
    },

    boxlist: {
        width: 350,
        height: 100,
        marginBottom: 40,
        borderRadius: 5,
        shadowOpacity: 0.5,
        shadowRadius: 15.5,
        backgroundColor: "white",
    },
    Hasil : {
        position: 'absolute',
        left: 15,
        top: 5,
        fontWeight:'Bold',
    },

    Hasil2 : {
        fontSize: 21,
        position: 'absolute',
        left: 40,
        top: 65,
        fontWeight:'Bold',
    },

    Hasil3 : {
        fontsize: 20,
        position: 'absolute',
        left: 237,
        top: 73,
        fontWeight: 'Bold',
        color: 'blue',
    },
      
    row:{
        flexDirection: "row",
        justifyContent: 'space-between',
        backgroundColor: 'powderblue',
        
    },
    boxtop:{
        alignItems:'center',
        //justifyContent:'center',
        backgroundColor: 'gray',
        height: 150,
    },
    textSubtittle:{
        fontFamily: "Cochin",
        fontSize: 16,
        fontWeight: "bold",
    },
    textTitle:{
        fontFamily: "Cochin",
        fontSize: 32,
        fontWeight: "bold",
    },
    marginMain:{
        marginVertical: 20,
        marginHorizontal: 10,
    },
    viewCard:{
        flexDirection: "row",
        padding: 10,
    },
    viewImage:{
        flex: 1,
    },
});


export default Screen2;
