import React, {useState} from 'react';
import {
    Text,
    View,
    TextInput,
    Button,
} from 'react-native';

const ScreenA = ({navigation}) => {
  function ScreenA({ navigation }){
    const [Keberangkatan, onChangeKeberangkatan] = React.useState('');
    const [Tujuan, onChangeTujuan] = React.useState('');
    const [Jadwal, onChangeJadwal] = React.useState('');

    const cariData = () => {
      navigation.navigate('Hasil Pencarian',{
        brgkt : Keberangkatan,
        tjn : Tujuan,
        jdwl : Jadwal,
      })
    }
  }
  
  return(
    <ImageBackground source={BackImage} style={styles.container}>
      <Text style={styles.namaapk}>mrar.id</Text>
      <View style={styles.bgbox}>
        
        <Text style={styles.teksDiBox}>Lokasi Keberangkatan</Text>
        
        <TextInput 
        style={styles.inputteks} 
        placeholder='  masukkan lokasi keberangkatan'
        onChangeText={(text) => onChangeKeberangkatan(text)}
        value={Keberangkatan}
        />

        <Text style={styles.teksDiBox}>Lokasi Tujuan</Text>
        <TextInput 
        style={styles.inputteks} 
        placeholder='  masukkan lokasi Tujuan' 
        onChangeText={(text) => onChangeTujuan(text)}
        value={Tujuan}
        />    
        
        <Text style={styles.teksDiBox}>Tanggal Keberangkatan</Text>
        <TextInput 
        style={styles.inputteks} 
        placeholder='  masukkan Tanggal keberangkatan'
        onChangeText={(text) => onChangeJadwal(text)}
        value={Jadwal}
        />
        
        <Button 
          title="Cari"
          onPress={cariData}    
          style={styles.tombol}
        />
      </View>
        <Text style={styles.namanim}>Copyright: M. Rafli - 119140124</Text>
    </ImageBackground>
  )
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 24,
    alignItems: 'center'
  },

  namaapk: {
    marginTop: 50,
    fontSize: 30,
    textAlign: 'center',
    fontWeight: "bold",
    color: "white"
  },

  bgbox: {
    width: 300,
    marginTop: 50,
    paddingVertical: 20,
    paddingHorizontal: 23,
    borderRadius: 10,
    shadowOpacity: 0.5,
    shadowRadius: 15.5,
    backgroundColor: "white",
    alignItems: 'center'
    
  },
  teksDiBox: {
    color: "black",
    alignSelf: 'flex-start',
    fontSize: 15,
    fontWeight: "bold",
    paddingVertical: 5,
    },

  inputteks: {
    height: 40,
    width: 260,
    borderWidth: 1,
    borderRadius: 5,
    borderColor: '#555',
    marginBottom: 5, 
    fontSize: 10,
    fontWeight: 'bold',
  },

  tombol:{
    height: 35,
    width: 260,
    borderRadius: 5,
    backgroundColor: "orange", 
    paddingVertical: 7,
    marginTop: 20
  },

  tekstombol: {
    fontsize: 15,
    color: '#fff',
    fontWeight: '500',
    textAlign: 'center'
  },

  namanim: {
    fontSize: 15,
    textAlign: 'center',
    fontWeight: "bold",
    color: "black",
  },
  Hasil : {
    position: 'absolute',
    left: 15,
    top: 5,
    fontWeight:'Bold',
  },

  Hasil2 : {
    fontSize: 21,
    position: 'absolute',
    left: 40,
    top: 65,
    fontWeight:'Bold',
  },

  Hasil3 : {
    fontsize: 20,
    position: 'absolute',
    left: 237,
    top: 73,
    fontWeight: 'Bold',
    color: 'blue',
  },

  imagestyle:{
    height: 40,
    width: 40,
  },

  boxlist: {
    width: 350,
    height: 100,
    marginBottom: 40,
    borderRadius: 5,
    shadowOpacity: 0.5,
    shadowRadius: 15.5,
    backgroundColor: "white",
  }
});

export default ScreenA;
