import React from 'react';
import { useRoute } from '@react-navigation/native';
import {
  FlatList,
  ImageBackground,
  StyleSheet, 
  Text, 
  View,
} from 'react-native';
import { JadwalPenerbangan } from '../data/data';
import BackImage from '../assets/airplane-2748431_960_720.png';

const ScreenB = ({navigation}) => {
  const route = useRoute();

  // return (
  //   <Text style={styles.namaapk}>mrar.id</Text>
  // )

  function NoHaveData() {
    return (        
      <Text>Maaf Hasil Pencarian Tidak Ditemukan</Text>  
    );
  }

  function DataGet (hasill){
    return (
      <ImageBackground source={BackImage} style={styles.container}>
        <Text style={styles.namaapk}>mrar.id</Text>
          <FlatList
            data={hasill}
            renderItem={({ item }) => (
              <View style={styles.boxlist}>
                <Text style={styles.Hasil}>({item.keberangkatan})   -   ({item.Tujuan})</Text>
                <Text style={styles.Hasil2}>{item.maskapai}</Text>
                <Text style={styles.Hasil3}>({item.Jadwal})</Text>
              </View>
            )}
          />
        </ImageBackground>
      );
  }

  function FindData(){
    if (route.params.brgkt === '' || route.params.tjn === '' || route.params.jdwl === ''){
      return NoHaveData();
    }else{
      const keberangkatanID = JadwalPenerbangan.find(id => id.keberangkatan.toLowerCase() === route.params.brgkt.toLowerCase())
      const tujuanID = JadwalPenerbangan.find(id => id.Tujuan.toLowerCase() === route.params.tjn.toLowerCase())
      const hasill = JadwalPenerbangan.filter(id => id.Jadwal.toLowerCase() === keberangkatanID.toLowerCase() && id.Tujuan.toLowerCase() === tujuanID.toLowerCase() && id.Jadwal === route.params.jdwl.toLowerCase());

      let findAll = (hasill.length >= 1) ? DataGet(hasill) : NoHaveData() ;

      return findAll;
    }
  }
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 24,
    alignItems: 'center'
  },

  namaapk: {
    marginTop: 30,
    marginBottom: 20,
    fontSize: 30,
    textAlign: 'center',
    fontWeight: "bold",
    color: "white"
  },

  bgbox: {
    width: 300,
    marginTop: 50,
    paddingVertical: 20,
    paddingHorizontal: 23,
    borderRadius: 10,
    shadowOpacity: 0.5,
    shadowRadius: 15.5,
    backgroundColor: "white",
    alignItems: 'center'
    
  },
  teksDiBox: {
    color: "black",
    alignSelf: 'flex-start',
    fontSize: 15,
    fontWeight: "bold",
    paddingVertical: 5,
    },

  inputteks: {
    height: 40,
    width: 260,
    borderWidth: 1,
    borderRadius: 5,
    borderColor: '#555',
    marginBottom: 5, 
    fontSize: 10,
    fontWeight: 'bold',
  },

  tombol:{
    height: 35,
    width: 260,
    borderRadius: 5,
    backgroundColor: "orange", 
    paddingVertical: 7,
    marginTop: 20
  },

  tekstombol: {
    fontsize: 15,
    color: '#fff',
    fontWeight: '500',
    textAlign: 'center'
  },

  namanim: {
    fontSize: 15,
    textAlign: 'center',
    fontWeight: "bold",
    color: "black",
  },
  Hasil : {
    position: 'absolute',
    left: 15,
    top: 5,
    fontWeight:'Bold',
  },

  Hasil2 : {
    fontSize: 21,
    position: 'absolute',
    left: 40,
    top: 65,
    fontWeight:'Bold',
  },

  Hasil3 : {
    fontsize: 20,
    position: 'absolute',
    left: 237,
    top: 73,
    fontWeight: 'Bold',
    color: 'blue',
  },

  imagestyle:{
    height: 40,
    width: 40,
  },

  boxlist: {
    width: 350,
    height: 100,
    marginBottom: 40,
    borderRadius: 5,
    shadowOpacity: 0.5,
    shadowRadius: 15.5,
    backgroundColor: "white",
  }
});

export default ScreenB;
