import React, {useState} from "react";
import {
  FlatList,
  ImageBackground,
  StyleSheet, 
  Text, 
  TextInput, 
  TouchableOpacity, 
  View,
} from 'react-native';
import BackImage from './assets/airplane-2748431_960_720.png';
import { NavigationContainer } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";
import { Button } from "react-native-web";

const stack = createStackNavigator();

function ScreenA({ navigation }){
    const [Keberangkatan, onChangeKeberangkatan] = React.useState('');
    const [Tujuan, onChangeTujuan] = React.useState('');
    const [Jadwal, onChangeJadwal] = React.useState('');

    const cariData = () => {
      navigation.navigate('Hasil Pencarian',{
        brgkt : Keberangkatan,
        tjn : Tujuan,
        jdwl : Jadwal,
      })
    }


  return(
    <ImageBackground source={BackImage} style={styles.container}>
      <Text style={styles.namaapk}>mrar.id</Text>
      <View style={styles.bgbox}>

        <Text style={styles.teksDiBox}>Lokasi Keberangkatan</Text>

        <TextInput 
        style={styles.inputteks} 
        placeholder='  masukkan lokasi keberangkatan'
        onChangeText={(text) => onChangeKeberangkatan(text)}
        value={Keberangkatan}
        />

        <Text style={styles.teksDiBox}>Lokasi Tujuan</Text>
        <TextInput 
        style={styles.inputteks} 
        placeholder='  masukkan lokasi Tujuan' 
        onChangeText={(text) => onChangeTujuan(text)}
        value={Tujuan}
        />    

        <Text style={styles.teksDiBox}>Tanggal Keberangkatan</Text>
        <TextInput 
        style={styles.inputteks} 
        placeholder='  masukkan Tanggal keberangkatan'
        onChangeText={(text) => onChangeJadwal(text)}
        value={Jadwal}
        />

        <Button 
          title="Cari"
          onPress={cariData}    
          style={styles.tombol}
        />
      </View>
        <Text style={styles.namanim}>Copyright: M. Rafli - 119140124</Text>
    </ImageBackground>
  )
}

function ScreenB({navigation}){
  const JADWAL = [
    {
      jadwal_id: "J-001",
      bandara_id_keberangkatan: "B01",
      bandara_id_kedatangan: "B02",
      maskapai_id: "M01",
      tanggal: "2020-01-07",
    },
    {
      jadwal_id: "J-002",
      bandara_id_keberangkatan: "B01",
      bandara_id_kedatangan: "B02",
      maskapai_id: "M02",
      tanggal: "2020-01-07",
    },
    {
     jadwal_id: "J-003",
     bandara_id_keberangkatan: "B01",
     bandara_id_kedatangan: "B02",
     maskapai_id: "M03",
     tanggal: "2020-01-01",
    },
    {
     jadwal_id: "J-004",
     bandara_id_keberangkatan: "B03",
     bandara_id_kedatangan: "B01",
     maskapai_id: "M01",
     tanggal: "2020-01-01",
    },
    {
      jadwal_id: "J-005",
      bandara_id_keberangkatan: "B02",
      bandara_id_kedatangan: "B03",
      maskapai_id: "M02",
      tanggal: "2020-01-02",
    },
    {
      jadwal_id: "J-006",
      bandara_id_keberangkatan: "B01",
      bandara_id_kedatangan: "B03",
      maskapai_id: "M02",
      tanggal: "2020-01-02",
    },
    {
      jadwal_id: "J-007",
      bandara_id_keberangkatan: "B02",
      bandara_id_kedatangan: "B01",
      maskapai_id: "M03",
      tanggal: "2020-01-03",
    },
    {
      jadwal_id: "J-008",
      bandara_id_keberangkatan: "B02",
      bandara_id_kedatangan: "B01",
      maskapai_id: "M01",
      tanggal: "2020-01-03",
    },
    {
      jadwal_id: "J-009",
      bandara_id_keberangkatan: "B01",
      bandara_id_kedatangan: "B03",
      maskapai_id: "M01",
