import React from "react";
import {
    Pressable,
    View,
    Text,
    TextInput,
    TouchableOpacity,
} from 'react-native';

export default function Cari({navigation}){
    const onPressHandler = () => {
      navigation.navigate("Hasil Pencarian");
    }
    return(
        <View style={styles.container}>
            <Text style={styles.namaapk}>mrar.id</Text>
            <View style={styles.bgbox}>
                <Text style={styles.teksDiBox}>Lokasi Keberangkatan</Text>
                <TextInput style={styles.inputteks} placeholder='  masukkan lokasi keberangkatan' />
                <Text style={styles.teksDiBox}>Lokasi Tujuan</Text>
                <TextInput style={styles.inputteks} placeholder='  masukkan lokasi Tujuan' />    
                <Text style={styles.teksDiBox}>Tanggal Keberangkatan</Text>
                <TextInput style={styles.inputteks} placeholder='  masukkan Tanggal keberangkatan'/>
                <TouchableOpacity style={styles.tombol}>
                    <Text style={styles.tekstombol}>Cari</Text>
                </TouchableOpacity>
            </View>
            <Text style={styles.namanim}>Copyright: M. Rafli - 119140124</Text>
            <text>ini screen untuk mencari jadwal</text>
            <Pressable 
            onPress={onPressHandler}
            style={({ pressed }) => ({backgroundColor: pressed ? '#ddd' : '#0f0'})}>
            <text>pergi ke hasil pencarian</text>
            </Pressable>
        </View>
    )
}
const styles = StyleSheet.create({
    container: {
      flex: 1,
      padding: 24,
      alignItems: 'center'
    },
  
    namaapk: {
      padding: 20,
      fontSize: 30,
      textAlign: 'center',
      fontWeight: "bold",
      color: "white"
    },
  
    bgbox: {
      width: 300,
      marginTop: 50,
      paddingVertical: 20,
      paddingHorizontal: 23,
      borderRadius: 10,
      shadowOpacity: 0.5,
      shadowRadius: 15.5,
      backgroundColor: "white",
      alignItems: 'center'
      
    },
    teksDiBox: {
      color: "black",
      alignSelf: 'flex-start',
      fontSize: 15,
      fontWeight: "bold",
      paddingVertical: 5,
      },
  
    inputteks: {
      height: 40,
      width: 260,
      borderWidth: 1,
      borderRadius: 5,
      borderColor: '#555',
      marginBottom: 5, 
      fontSize: 10,
      fontWeight: 'bold',
    },
  
    tombol:{
      height: 35,
      width: 260,
      borderRadius: 5,
      backgroundColor: "orange", 
      paddingVertical: 7,
      marginTop: 20
    },
  
    tekstombol: {
      fontsize: 15,
      color: '#fff',
      fontWeight: '500',
      textAlign: 'center'
    },
  
    namanim: {
      padding: '200',
      fontSize: 15,
      textAlign: 'center',
      fontWeight: "bold",
      color: "black",
    },
  });
