import React from 'react';
import { useRoute } from '@react-navigation/native';
import {
  FlatList,
  ImageBackground,
  StyleSheet, 
  Text, 
  View,
} from 'react-native';
import { MASKAPAI, BANDARA, JADWAL } from '../data/data';
import BackImage from '../assets/airplane-2748431_960_720.png';

const ScreenB = ({navigation}) => {
  const route = useRoute();

  return (
    <Text style={styles.namaapk}>mrar.id</Text>
  )

  function NoHaveData() {
    return (        
      <Text>Maaf Hasil Pencarian Tidak Ditemukan</Text>  
    );
  }

  function DataGet (hasil){
    return (
      <ImageBackground source={BackImage} style={styles.container}>
        <FlatList
          data = {hasil}
          renderItem={({item}) => (
            <View style={styles.boxlist}>
              <Text>{BANDARA.find(sub => sub.bandara_id == item.bandara_id_keberangkatan).bandara_nama}</Text>
              <Text>{BANDARA.find(sub => sub.bandara_id === item.bandara_id_kedatangan).bandara_nama}</Text>
              <Text>{MASKAPAI.find(sub => sub.maskapai_id === item.maskapai_id).maskapai_nama}</Text>
            </View>
          )}
          keyExtractor={item => item.jadwal_id}
        />
      </ImageBackground>
    );
  }
  function FindData(){
    if (route.params.brgkt === '' || route.params.tjn === '' || route.params.jdwl === ''){
      return NoHaveData();
    }else{
      const keberangkatanID = BANDARA.find(id => id.bandara_nama.toLowerCase() === route.params.brgkt.toLowerCase()).bandara_id;
      const tujuanID = BANDARA.find(id => id.bandara_nama.toLowerCase() === route.params.tjn.toLowerCase()).bandara_id;
      const hasil = JADWAL.filter(id => id.bandara_id_keberangkatan.toLowerCase() === keberangkatanID.toLowerCase() && id.bandara_id_kedatangan.toLowerCase() === tujuanID.toLowerCase() && id.jdwl === route.params.jdwl.toLowerCase());

      let findAll = (hasil.length >= 1) ? DataGet(hasil) : NoHaveData() ;

      return findAll;
    }
  }
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 24,
    alignItems: 'center'
  },

  namaapk: {
    marginTop: 50,
    fontSize: 30,
    textAlign: 'center',
    fontWeight: "bold",
    color: "white"
  },

  bgbox: {
    width: 300,
    marginTop: 50,
    paddingVertical: 20,
    paddingHorizontal: 23,
    borderRadius: 10,
    shadowOpacity: 0.5,
    shadowRadius: 15.5,
    backgroundColor: "white",
    alignItems: 'center'
    
  },
  teksDiBox: {
    color: "black",
    alignSelf: 'flex-start',
    fontSize: 15,
    fontWeight: "bold",
    paddingVertical: 5,
    },

  inputteks: {
    height: 40,
    width: 260,
    borderWidth: 1,
    borderRadius: 5,
    borderColor: '#555',
    marginBottom: 5, 
    fontSize: 10,
    fontWeight: 'bold',
  },

  tombol:{
    height: 35,
    width: 260,
    borderRadius: 5,
    backgroundColor: "orange", 
    paddingVertical: 7,
    marginTop: 20
  },

  tekstombol: {
    fontsize: 15,
    color: '#fff',
    fontWeight: '500',
    textAlign: 'center'
  },

  namanim: {
    fontSize: 15,
    textAlign: 'center',
    fontWeight: "bold",
    color: "black",
  },
  Hasil : {
    position: 'absolute',
    left: 15,
    top: 5,
    fontWeight:'Bold',
  },

  Hasil2 : {
    fontSize: 21,
    position: 'absolute',
    left: 40,
    top: 65,
    fontWeight:'Bold',
  },

  Hasil3 : {
    fontsize: 20,
    position: 'absolute',
    left: 237,
    top: 73,
    fontWeight: 'Bold',
    color: 'blue',
  },

  imagestyle:{
    height: 40,
    width: 40,
  },

  boxlist: {
    width: 350,
    height: 100,
    marginBottom: 40,
    borderRadius: 5,
    shadowOpacity: 0.5,
    shadowRadius: 15.5,
    backgroundColor: "white",
  }
});

export default ScreenB;
