import React from 'react';
import { useRoute } from '@react-navigation/native';
import {
  FlatList,
  StyleSheet, 
  Text, 
  View,
  SafeAreaView,
} from 'react-native';
import { BANDARA, MASKAPAI, JADWAL } from '../data/Data';
import BackImage from '../assets/airplane-2748431_960_720.png';

const Screen2 = ({navigation}) => {
  const route = useRoute();
  return (
    <SafeAreaView >
        <View style={styles.header}>
            <Text style={styles.namaapk}>mrar.id</Text>
            <Text style={styles.teksDiBox}>Hasil Pencarian Penerbangan</Text>
            <Text style={styles.teksDiBox}>(Tanggal Keberangkatan)</Text>
        </View>
        <View style={styles.marginMain}>                  
            <FindData/>
        </View>
    </SafeAreaView>
  )
  function NoHaveData() {
    return ( 
       <Text style={styles.textnohavedata}>Maaf Hasil Pencarian Tidak Ditemukan</Text>  
    );
  }

  function DataGet (hasil){
    return (
        <FlatList
            data = {hasil}
            renderItem={({item}) => (
            <ImageBackground source={BackImage} style={styles.container}>
                <View style={styles.boxlist}>
                    <Text style={styles.Hasil}>{BANDARA.find(src => src.bandara_id == item.bandara_id_keberangkatan).bandara_nama}  {'->'}  {BANDARA.find(sub => sub.bandara_id === item.bandara_id_kedatangan).bandara_nama}</Text>
                    <Text style={styles.Hasil2}>{MASKAPAI.find(src => src.maskapai_id === item.maskapai_id).maskapai_nama}</Text>
                    <Text style={styles.Hasil3}>{JADWAL.find(src => src.tanggal === item.tanggal).tanggal}</Text>
                </View>
            </ImageBackground>
            )}
            keyExtractor={item => item.jadwal_id}
        />
    );
  };


  function FindData(){
    if (route.params.keberangkatan === '' || route.params.tujuan === '' || route.params.waktu === ''){
        return NoHaveData();
    }else{
        const brgkt = BANDARA.find(id => id.bandara_nama.toLowerCase() === route.params.keberangkatan.toLowerCase()).bandara_id;
        const tjn = BANDARA.find(id => id.bandara_nama.toLowerCase() === route.params.tujuan.toLowerCase()).bandara_id;
        const HasilFinal = JADWAL.filter(id => id.bandara_id_keberangkatan.toLowerCase() === brgkt.toLowerCase() && id.bandara_id_kedatangan.toLowerCase() === tjn.toLowerCase() && id.tanggal === route.params.waktu.toLowerCase());

        let findAll = (HasilFinal.length >= 1) ? DataGet(HasilFinal) : NoHaveData() ;

        return findAll;
    }
  }
}
const styles = StyleSheet.create({

    container: {
        flex: 1,
        padding: 24,
        alignItems: 'center'
    },

    namaapk: {
        marginTop: 20,
        marginBottom: 20,
        fontSize: 30,
        textAlign: 'center',
        fontWeight: "bold",
        color: "white"
    },

    textnohavedata: {
        color: "black",
        fontSize: 15,
        fontWeight: 'bold',
        paddingVertical: 150,
        alignSelf: 'center'
    },

    teksDiBox: {
        color: "white",
        fontSize: 15,
        fontWeight: "bold",
        paddingVertical: 5,
    },

    boxlist: {
        width: 350,
        height: 100,
        marginBottom: 40,
        borderRadius: 5,
        shadowOpacity: 0.5,
        shadowRadius: 15.5,
        backgroundColor: "white",
    },
    Hasil : {
        position: 'absolute',
        left: 15,
        top: 5,
        fontWeight:'Bold',
    },

    Hasil2 : {
        fontSize: 21,
        position: 'absolute',
        left: 40,
        top: 65,
        fontWeight:'Bold',
    },

    Hasil3 : {
        fontsize: 20,
        position: 'absolute',
        left: 237,
        top: 73,
        fontWeight: 'Bold',
        color: 'blue',
    },
      
    header:{
        alignItems:'center',
        backgroundColor: 'gray',
        height: 150,
    }
});


export default Screen2;
